import React,{useState} from "react"

import ExpenseForm from "./ExpenseForm"
import {Card} from "../UI/Card"

const NewExpense = (prop)=>{
    const [isEditing,setIsEditing ]= useState(false)

    const startEditingHandler = () => {
        setIsEditing(true);
    };
    const stopEditingHandler = () => {
        setIsEditing(false);
    };
    
   

return ( 
<Card>
<div className = "new-expense"> {!isEditing && ( <button onClick = { startEditingHandler } > Add NewButton </button>)} 
       {isEditing && ( <ExpenseForm onSaveExpenseData = {prop.saveExpenseDataHandler } onClick = { stopEditingHandler}/>)}
        </div>
</Card>

)
}

export default NewExpense
