import ExpenseItem from "./components/Expenses/ExpenseItem";
import { Card } from "./components/UI/Card";
import ExpenseForm from "./components/ExpenseForm/ExpenseForm";
import { getElementError } from "@testing-library/react";

const App = () => {
  const expenses = [
    {
      id: "e1",
      title: "Toilet Paper",
      amount: 94.12,
      date: new Date(2020, 7, 14),
      LocationOfExpenditure: "Dhanya supermarket",
    },
    {
      id: "e2",
      title: "New TV",
      amount: 799.49,
      date: new Date(2021, 2, 12),
      LocationOfExpenditure: "Samsung Electronics",
    },
    {
      id: "e3",
      title: "Car Insurance",
      amount: 294.67,
      date: new Date(2021, 2, 28),
      LocationOfExpenditure: "LIC Kollam BRANCH",
    },
    {
      id: "e4",
      title: "New Desk (Wooden)",
      amount: 450,
      date: new Date(2021, 5, 12),
      LocationOfExpenditure: "ANI FURNITURES, TVM",
    },
  ];
  const array = [];
  for (let i = 0; i < expenses.length; i++) {
    array.push(
      <ExpenseItem
        title={expenses[i].title}
        amount={expenses[i].amount}
        date={expenses[i].date}
        LocationOfExpenditure={expenses[i].LocationOfExpenditure}
      ></ExpenseItem>
    );
  }

  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    console.log(expenseData);
    const adder = document.getElementById("display");
    const child = (
      <div>
        <ExpenseItem
          title={expenseData.title}
          amount={expenseData.amount}
          date={expenseData.date}
          LocationOfExpenditure={expenseData.LocationOfExpenditure}
        ></ExpenseItem>
      </div>
    );
    adder.appendChild(child);
  };

  return (
    <div>
      <ExpenseForm onSaveExpenseData={saveExpenseDataHandler}></ExpenseForm>
      <Card>
        <div id="display">{array}</div>
      </Card>
    </div>
  );
};

export default App;
